/**
 * The template for displaying Comments
 * The area of the page that contains comments and the comment form.
 
  */
// Exit if accessed directly
if ( !defined('ABSPATH')) exit;


?>

<?php if (post_password_required()) { ?>
    <p class="nocomments"><?php _e('Bài viết được bảo vệ, nhập mật khẩu để tiếp tục!', 'ht'); ?></p>
    
	<?php return; } ?>

<?php if (have_comments()) : ?>
    <h6 id="comments">
		<span class="title">
			<?php printf( __('%1$s Bình luận','ht'),number_format_i18n( get_comments_number())); ?>
		</span>
    </h6>

    

    <ol class="commentlist">
        <?php wp_list_comments('avatar_size=30&type=comment'); ?>
    </ol>
    
    <?php if ( get_comment_pages_count() > 1 && get_option( 'page_comments' ) ) : ?>
    <div class="navigation">
        <div class="previous"><?php previous_comments_link(__( '&#8249; Bình luận cũ hơn','ht' )); ?></div><!-- end of .previous -->
        <div class="next"><?php next_comments_link(__( 'Bình luận mới hơn &#8250;','ht', 0 )); ?></div><!-- end of .next -->
    </div><!-- end of.navigation -->
    <?php endif; ?>

<?php else : ?>

<?php endif; ?>

<?php
if (!empty($comments_by_type['pings'])) : // let's seperate pings/trackbacks from comments
    $count = count($comments_by_type['pings']);
    ($count !== 1) ? $txt = __('Pings&#47;Trackbacks','ht') : $txt = __('Pings&#47;Trackbacks','ht');
?>

    <h6 id="pings"><?php printf( __( '%1$d %2$s cho "%3$s"', 'ht' ), $count, $txt, get_the_title() )?></h6>

    <ol class="commentlist">
        <?php wp_list_comments('type=pings&max_depth=<em>'); ?>
    </ol>


<?php endif; ?>

<?php if (comments_open()) : ?>

    <?php
    $fields = array(
        'author' => '<p class="comment-form-author">' . '<input id="author" placeholder="' . __('Tên','ht') . '" name="author" type="text" value="' . esc_attr($commenter['comment_author']) . '" size="30" /></p>',
        'email' => '<p class="comment-form-email"><input id="email" placeholder="' . __('E-mail','ht') . '" name="email" type="text" value="' . esc_attr($commenter['comment_author_email']) . '" size="30" /></p>'
    );
	$defaults['title_reply'] = __('Bình Luận','ht');
	$defaults['cancel_reply_link'] = __('Hủy','ht');	
	$defaults['comment_notes_before'] = '';
    $defaults['fields'] = apply_filters('comment_form_default_fields', $fields);
	$defaults['comment_field'] = '<p class="comment-form-comment"><textarea id="comment" name="comment" cols="45" rows="8" aria-required="true" placeholder="' . __( 'Nội dung', 'ht' ) . '"></textarea></p>';
	$defaults['label_submit'] = __('Gửi','ht');
	$defaults['comment_notes_after'] = '';
	comment_form($defaults);
    ?>


<?php endif; ?>